import React from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col'
import './ImageGallery.css'
import {ImageButton} from '../ImageButton/ImageButton'
import {ImageViewer} from '../ImageViewer/ImageViewer'


export class ImageGallery extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            visible: false,
            photoSrc: "",
            photos: []
          };
          this.selectPhoto = this.selectPhoto.bind(this);
          this.componentDidMount()

    }

    getPhotos()
    {   
        Images = []
    for (let i = 0; i < this.state.photos.length; i++) {
       this.Images.push(
       <Col lg ={(3)} md={(4)} sm ={(6)} xs ={(12)}>
        <ImageButton  src = {this.state.photos[i]["url"]} alt ="test "
        onClick = {()=>{this.selectPhoto(this.state.photos[i])}}
        />
       </Col>
       )
        }
    }
    componentDidMount() {
        fetch('http://localhost:3001/images')
          .then(response => response.json())
          .then(data => this.setState({ 
              photos:data["media"]
            }));
            this.getPhotos();
      }

    selectPhoto(obj) {
        this.setState({
            visible: !this.state.visible,
            photoSrc: (obj!==undefined)?obj.src : "",
          });
      };

    render() { 
        return ( 
            <div className="GalleyContainer">
            <ImageViewer url ={this.state.photoSrc} visable ={this.state.visible} onClick = {()=>{this.selectPhoto(undefined)}}/>
            <div className="ImageContainer">
            <Container>
            <Row> 
            {(this.Images)}
            </Row>
            </Container>
         </div>
        </div>
        );
    }
}
