import React from 'react';
import Gallery from "react-photo-gallery";
import '../ImageGallery/ImageGallery.css'
import SelectedImage from "../ImageGallery/selectedImage";
import Viewer from 'react-viewer';
import 'react-viewer/dist/index.css';



export class ImageGallery extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            visible: false,
            photoSrc: "",
          };
          this.selectPhoto = this.selectPhoto.bind(this);

    }

    selectPhoto(event, obj) {
        this.setState({
            visible: !this.state.visible,
            photoSrc: obj.photo.src,
          });
      };

    render() { 
        return ( 
            <div className="GalleyContainer">
            <div className="closeViewer" onClick={this.selectPhoto}>
                <Viewer
                visible={this.state.visible}
                images={[{src: this.state.photoSrc, alt: ''}]}
                noNavbar = {true}
                noFooter ={true}
                drag ={false}
                onClose ={()=>this.selectPhoto}
                onMaskClick ={()=> this.selectPhoto}
                onClick = {()=>this.selectPhoto}
            />
            </div>
             <Gallery photos={photos}
             onClick={this.selectPhoto}
            renderImage={SelectedImage}
          />
            </div>
        );
    }
}



