import '../ImageParallax/ImageParallax.css'

const headerStyle =(
    color= "white",
    fontFamily = "Roboto, sans-serif,",
    textShadow = "-1px 0 black, 0 1px black, 1px 0 black, 0 -1px black",
    fontSize= "3em,",
    textAlign = "center",
    height= "50%",
    width =  "100%",
    position = "absolute",
    bottom= "0"
);

  export class ImageParallax extends React.Component {
    constructor(props) {
        super(props);
        this.state = {  }
    }

     title = (
      <div classnamee ="ParallaxDiv"  style={{ height: 500 }}>
        <h1>{this.props.title}</h1>
        </div> 
      )

    render() { 
        return ( 
        <Parallax bgImage={this.props.url} blur={{ min: -1, max: 3 }} >
          {this.title}
          </Parallax> );
    }
  }
