import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col'
import './ImageGallery.css'
import {ImageButton} from '../ImageButton/ImageButton'
import {ImageViewer} from '../ImageViewer/ImageViewer'
import res from 'http'



export class ImageGallery extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            visible: false,
            photoSrc: "",
            photos: null
          };
          this.selectPhoto = this.selectPhoto.bind(this);
          this.componentDidMount = this.componentDidMount.bind(this);
          this.componentDidMount();
          this.getPhotos();
    }

    componentDidMount() {
            let url= "http://localhost:3001/images";
             fetch(url).then(response => response.json())
            .then(data => 
                {
                        this.setState({
                            photos : data
                        });
                });
      }

    selectPhoto(obj) {
        this.setState({
            visible: !this.state.visible,
            photoSrc: (obj!==undefined)?obj.src : "",
          });
      };

    render() { 
        return ( 
            <div className="GalleyContainer">
            <ImageViewer url ={this.state.photoSrc} visable ={this.state.visible} onClick = {()=>{this.selectPhoto(undefined)}}/>
            {this.gallaryComponent}
            </div>
        );
    }

