import React from 'react'
import './ConectMe.css'
import Form from 'react-bootstrap/Form'
import FormControl from 'react-bootstrap/FormControl'
import FormGroup from 'react-bootstrap/FormGroup'
import FormLabel from 'react-bootstrap/FormLabel'
import FormText from 'react-bootstrap/FormText'
import Button from 'react-bootstrap/Button'
import $ from 'jquery/lib/node-jquery'
export class ContectMe extends React.Component {
    constructor(props) {
        super(props);
        this.state = { 
         }
         this.sendEmail = this.sendEmail.bind(this);
    }

     sendEmail() {
      let inputName = document.getElementById('nameInput').value
      let inoutPhone = document.getElementById('formPhoneNumber').value
      let inputhowDidYouHeardAboutMe = document.getElementById('howDidYouHeardAboutMe').value
      $.ajax({
        method: "POST",
        url: 'http://localhost:3001',
        data: 
        {
          "name":inputName,
          "phone":inoutPhone,
          "heard":inputhowDidYouHeardAboutMe
        },
        dataType: 'json',
        cache: false,
        processData: false,
        contentType: false
    }).done((data) => {
        //resolve(data);
    }).fail((err) => {
        //console.log("errorrr for file upload", err);
        //reject(err);
    });
    }

    render() { 
        return ( 
            <div className="ContectSection"> 
            <h1>Contect Me</h1>
       <Form className='ContectMeForm'> 
         <FormGroup controlId="nameInput">
         <FormLabel>Name</FormLabel>
        <FormControl type="Name" placeholder="Enter You Name" />
        <FormText className="text-muted">
          We'll never share your email with anyone else.
        </FormText>
         </FormGroup>
         <FormGroup controlId="formPhoneNumber">
        <FormLabel>Phone Number</FormLabel>
         <Form.Control type="number" placeholder="Phone Number" />
        </FormGroup>
        <FormLabel>How Did you heard About me?</FormLabel>
        <FormGroup controlId="howDidYouHeardAboutMe" >
        <FormControl as="select">
        <option>1</option>
         <option>2</option>
        <option>3</option>
        <option>4</option>
        <option>5</option>
        </FormControl>
        </FormGroup>
        <Button variant="primary" type="button" onClick={this.sendEmail} >
         Submit
        </Button>
       </Form>
       </div>
         );
    }
}
