import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col'
import '../ImageGallery/ImageGallery.css'
import {ImageButton} from '../ImageButton/ImageButton'
import {ImageViewer} from '../ImageViewer/ImageViewer'



export class ImageGallery extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            visible: false,
            photoSrc: "",
            users: []
          };
          this.selectPhoto = this.selectPhoto.bind(this);
          this.getPhotos();
    }


    selectPhoto(obj) {
        this.setState({
            visible: !this.state.visible,
            photoSrc: (obj!==undefined)?obj.src : "",
          });
      };

    render() { 
        return ( 
            <div className="GalleyContainer">
            <ImageViewer url ={this.state.photoSrc} visable ={this.state.visible} onClick = {()=>{this.selectPhoto(undefined)}}/>
            {this.gallaryComponent}
            </div>
        );
    }

